import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { Prospect } from 'src/app/models/prospect';
import { Region } from 'src/app/models/region';
import { ProspectService } from 'src/app/services/prospect.service';
import { RegionService } from 'src/app/services/region.service';
import { StatutService } from 'src/app/services/statut.service';

@Component({
  selector: 'app-ajout-prospect',
  templateUrl: './ajout-prospect.component.html',
  styleUrls: ['./ajout-prospect.component.scss']
})
export class AjoutProspectComponent implements OnInit {

  prospect!: Prospect;
  region!: Region;
  regions!: Region[];
  idRegion!: number;

  constructor(private prospectService: ProspectService, private statutService: StatutService, private regionService: RegionService, private route: Router) { }

  addProspect() {
    this.statutService.getById(1).subscribe(
      response => {
        this.regionService.getById(this.idRegion).subscribe

        this.prospect.statut = response;
        this.prospectService.merge(this.prospect).subscribe(response => {
          window.location.reload();
        });
      });
  }

  getAllRegions() {
    this.regionService.getAll().subscribe(response =>
      this.regions = response
    );
  }


  ngOnInit(): void {
    this.idRegion = 0;
    this.prospect = new Prospect();
    this.getAllRegions();
  }

  infor(f: NgForm) {
    this.addProspect();
  }

}
<h1 class="text-center">Contactez-nous !</h1>

<div class="container">

    <form #f="ngForm" (ngSubmit)="infor(f)">

        <fieldset>

            <legend class="text-center">Vos coordonnées</legend>

            <div class="mb-3">
                <label for="nom">Nom</label>
                <input type="text" class="form-control" id="nom" name="nom" [(ngModel)]="prospect.nom" />
            </div>

            <div class="mb-3">
                <label for="prenom">Prenom</label>
                <input type="text" class="form-control" id="prenom" name="prenom" [(ngModel)]="prospect.prenom" />
            </div>

            <div class="mb-3">
                <label for="email">Adresse email</label>
                <input type="email" class="form-control" id="email" name="email" [(ngModel)]="prospect.email" />
            </div>

            <div class="mb-3">
                <label for="adresse">Adresse postale</label>
                <input type="text" class="form-control" id="adresse" name="adresse" [(ngModel)]="prospect.adresse" />
            </div>

            <div class="mb-3">
                <label for="telephone">Téléphone</label>
                <input type="text" class="form-control" id="telephone" name="telephone" [(ngModel)]="prospect.telephone" />
            </div>

            <div class="mb-3">
                <label for="idRegion" class="form-label">Région</label>
                <select [(ngModel)]="idRegion" name="idRegion" class="form-control">
                    <option [value]="0" disabled>---Selectionnez une région---</option>
                    <option *ngFor="let region of regions" [value]="region.id">{{region.nom}}</option>
                </select>
            </div>

            <div class="mb-3 text-center">
                <input class="btn btn-primary" type="submit" value="Envoyer" />
            </div>

        </fieldset>

    </form>

</div>
